import { useState } from 'react'

const Tasks = () => {
    const [tasks, setTasks] = useState([
        {
            id:1,
            text:'Doctors appointment',
            day:'Feb 5th at 2:30pm',
            reminder:'true',
        },
        {
            id:2,
            text:'Meeting at school',
            day:'Feb 6th at 2:30pm',
            reminder:'true',
        },
        {
            id:3,
            text:'Food shopping',
            day:'Feb 5th at 2:30pm',
            reminder:'flase',
        }
    ])

    return (
        <>
            {tasks.map((task)=>(
                <h3 key={task.id}>{task.text}</h3>
            ))}
        </>
    )
}

export default Tasks
