import PropTypes from 'prop-types'
import Button from './Button'

const Header = ({ title, onAdd, showAdd }) => {
    // const onClick = () => {
    //     console.log("clicked")
    // }

    return (
        <header className='header'>
            <h1>{title}</h1>
            <Button color='green' text={showAdd ? 'Close' : ''} onClick={onAdd}/>

        </header>
    )
}

Header.defaultProps = {
    title: 'Task Tracker'
}
Header.propTypes = {
    title: PropTypes.string.isRequired, // title has to be a string
}

// const headingstyle = {
